settings.colorFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', percent).toHexString();
	}
	return 'blue';
}

settings.markerImageFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'thermometer') {
	var res = {
	    url: images[0],
	    size: 40
	}
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120;
	    var index = Math.min(3, Math.floor(4 * percent));
	    res.url = images[index];
	}
	return res;
}

settings.cellContentFunction:
var schedule = JSON.parse(value);
var dateFormat;
if (new Date().getYear() === new Date(schedule.startTime).getYear()) {
    dateFormat = 'd MMM';
} else {
    dateFormat = 'd MMM yyyy';
}
return ctx.date.transform(schedule.startTime, dateFormat);

var schedule = JSON.parse(value);
var time = ctx.date.transform(schedule.startTime, 'HH:mm');
return '<span style="background: rgba(111, 116, 242, 0.08); border-radius: 16px; padding: 4px 8px; color: #5553AF;">'+time+'</span>';

var schedulerWeekday =
  [
    'scheduler.sunday',
    'scheduler.monday',
    'scheduler.tuesday',
    'scheduler.wednesday',
    'scheduler.thursday',
    'scheduler.friday',
    'scheduler.saturday'
  ];
var schedule = JSON.parse(value);
if (schedule.repeat) {
    var type = schedule.repeat.type;
    if (type === 'DAILY') {
        return ctx.translate.instant('scheduler.daily');
    } else if (type === 'EVERY_N_DAYS') {
        return ctx.translate.instant('scheduler.every-n-days-text', {days: schedule.repeat.days});
    } else if (type === 'MONTHLY') {
        return ctx.translate.instant('scheduler.monthly');
    } else if (type === 'EVERY_N_WEEKS') {
        return ctx.translate.instant('scheduler.every-n-weeks-text', {weeks: schedule.repeat.weeks});
    } else if (type === 'YEARLY') {
        return ctx.translate.instant('scheduler.scheduler.yearly');
    } else if (type === 'TIMER') {
        var timeUnit = schedule.repeat.timeUnit;
        var timeUnitRepeatText;
        if (timeUnit === 'HOURS') {
            timeUnitRepeatText = 'scheduler.every-hour';
        } else if (timeUnit === 'MINUTES') {
            timeUnitRepeatText = 'scheduler.every-minute';
        } else {
            timeUnitRepeatText = 'scheduler.every-second';
        }
        return ctx.translate.instant(timeUnitRepeatText, {count: schedule.repeat.repeatInterval});
    } else {
        var text = ctx.translate.instant('scheduler.weekly') + ' ' + ctx.translate.instant('scheduler.on') + ' ';
        text += schedule.repeat.repeatOn.map((day) => ctx.translate.instant(schedulerWeekday[day])).join(', ');
        return text;
    }
} else {
    return '&#8212;';
}

var schedule = JSON.parse(value);
if (schedule.repeat && schedule.repeat.endsOn) {
    var dateFormat;
    if (new Date().getYear() === new Date(schedule.repeat.endsOn).getYear()) {
        dateFormat = 'd MMM';
    } else {
        dateFormat = 'd MMM yyyy';
    }
    return ctx.date.transform(schedule.repeat.endsOn, dateFormat);
} else {
    return '&#8212;';
}

var configuration = JSON.parse(value);
var duration;
if (configuration.msgBody && configuration.msgBody.durationInMinutes) {
    duration = configuration.msgBody.durationInMinutes + ' min';
} else {
    duration = '&#8212;';
}

return '<span style="background: rgba(111, 116, 242, 0.08); border-radius: 16px; padding: 4px 8px;  color: #5553AF;">'+duration+'</span>';

var configuration = JSON.parse(value);
var volume;
if (configuration.msgBody && configuration.msgBody.volumeInLitters) {
    volume = configuration.msgBody.volumeInLitters + ' l';
} else {
    volume = '&#8212;';
}

return '<span style="background: rgba(111, 116, 242, 0.08); border-radius: 16px; padding: 4px 8px; color: #5553AF;">'+volume+'</span>';

