settings.colorFunction:
var type = dsData[dsIndex]['Type'];
if (type == 'colorpin') {
	var temperature = dsData[dsIndex]['temperature'];
	if (typeof temperature !== undefined) {
	    var percent = (temperature + 60)/120 * 100;
	    return tinycolor.mix('blue', 'red', amount = percent).toHexString();
	}
	return 'blue';
}

settings.labelFunction:
var deviceType = data['Type'];
var coordinates = data['coordinates'];
var status = 'normal';
if (deviceType === 'Smart Shelf') {
    if (coordinates && coordinates.length === 4) {
        var color = getColor(status);
        var label = 'Vegetables';
        return '<span style="margin-left: 100px; margin-top: -50px; color: '+ color +';">' + label + '</span>';
    }
}
return '';

function getColor(status) {
    switch(status) {
        case 'major':
            return '#D5C21B';
        case 'critical':   
            return '#E64A19';
        case 'off':
        case 'disconnected':
            return '#CAC1D2';
        case 'normal':
        default:    
            return '#817889';
    }
}

