function getColor(status) {
    switch(status) {
        case 'major':
            return '#D5C21B';
        case 'critical':   
            return '#E64A19';
        case 'off':
        case 'disconnected':
            return '#CAC1D2';
        case 'normal':
        default:    
            return '#817889';
    }
}

