    supermarket.name = title;
    widgetContext.assetService.saveAsset(supermarket, null, {ignoreLoading: true}).subscribe(() => {
       var stateParams = widgetContext.stateController.getStateParams();
       stateParams.selectedSupermarket.entityName = title;
