function openDeleteCustomerDialog() {
  var title = 'Are you sure you want to delete the company ' + entityName + '?';
  var content = 'Be careful, after the confirmation, the company and all related data will become unrecoverable!';
  dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
    function(result) {
      if (result) {
        deleteCustomer();
      }
    }
  );
}

function deleteCustomer() {
  customerService.deleteCustomer(entityId.id).subscribe(
    function() {
      widgetContext.updateAliases();
    }
  );
}

