function openAddCompanyDialog() {
  customDialog.customDialog(htmlTemplate, AddCompanyDialogController).subscribe();
}

function AddCompanyDialogController(instance) {
  let vm = instance;

  vm.addCompanyFormGroup = vm.fb.group({
    title: ['', [vm.validators.required, vm.validators.maxLength(255)]]
  });

  vm.addCompanyFormGroup.addControl('country', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('city', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('state', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('zip', vm.fb.control('',
      zipValidators('')
  ));
  vm.addCompanyFormGroup.addControl('address', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('address2', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('phone', vm.fb.control('', []));
  vm.addCompanyFormGroup.addControl('email', vm.fb.control('', [vm.validators.email]));


  vm.addCompanyFormGroup.get('country').valueChanges.subscribe(
      (country) => {
        vm.addCompanyFormGroup.get('zip').setValidators(zipValidators(country));
        vm.addCompanyFormGroup.get('zip').updateValueAndValidity({onlySelf: true});
        vm.addCompanyFormGroup.get('zip').markAsTouched({onlySelf: true});
      }
  );


  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    vm.addCompanyFormGroup.markAsPristine();
    saveCompanyObservable().subscribe(
      function (customer) {
          widgetContext.rxjs.forkJoin([
              saveAttributes(customer),
              saveUserGroups(customer.id)
          ]).subscribe(
              function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
              }
        );
      }
    );
  };

  function zipValidators(country) {
    const zipValidators = [];
    if (country && POSTAL_CODE_PATTERNS[country]) {
      const postalCodePattern = POSTAL_CODE_PATTERNS[country];
      zipValidators.push(vm.validators.pattern(postalCodePattern));
    }
    return zipValidators;
  }

  function saveCompanyObservable() {
      return getOrCreateSmartRetailCustomerGroup().pipe(
          widgetContext.rxjs.switchMap((smartRetail) => {
              const formValues = vm.addCompanyFormGroup.value;
              let customer = {
                  title: formValues.title,
                  country: formValues.country,
                  city: formValues.city,
                  state: formValues.state,
                  zip: formValues.zip,
                  address: formValues.address,
                  address2: formValues.address2,
                  phone: formValues.phone,
                  email: formValues.email
              };
              return customerService.saveCustomer(customer, smartRetail.id.id);
          })
      );
  }

  function getOrCreateSmartRetailCustomerGroup() {
      return getEntityGroupByName("Smart Retail", "CUSTOMER").pipe(
          widgetContext.rxjs.switchMap((group) => {
              if (group) {
                  return widgetContext.rxjs.of(group);
              } else {
                  var smartRetail = {
                    type: 'CUSTOMER',
                    name: 'Smart Retail'
                  };
                  return entityGroupService.saveEntityGroup(smartRetail);
              }
          })
      );
  }

  function saveUserGroups(customerId) {

      return widgetContext.rxjs.forkJoin([
         getRoleByName("Smart Retail Read Only", "GROUP"),
         getRoleByName("Smart Retail User", "GENERIC"),
         getRoleByName("Smart Retail Administrator", "GENERIC"),
         getEntityGroupByName("Supermarket Users Shared", "DASHBOARD"),
         getEntityGroupByName("Supermarket Administrators Shared", "DASHBOARD")
      ]).pipe(widgetContext.rxjs.switchMap((data) => {

            var smartRetailReadOnlyGroupRole = data[0];
            var smartRetailUserGenericRole = data[1];
            var smartRetailAdministratorGenericRole = data[2];
            var smartSupermarketSharedGroup = data[3];
            var smartSupermarketGroup = data[4];

            var smartRetailUsers = {
              type: 'USER',
              name: 'Smart Retail Users',
              ownerId: customerId
            };

            var smartRetailAdministrators = {
              type: 'USER',
              name: 'Smart Retail Administrators',
              ownerId: customerId
            };

            return widgetContext.rxjs.forkJoin([
                entityGroupService.saveEntityGroup(smartRetailUsers),
                entityGroupService.saveEntityGroup(smartRetailAdministrators)
            ]).pipe(widgetContext.rxjs.switchMap((data) => {
                var smartRetailUsersGroup = data[0];
                var smartRetailAdministratorsGroup = data[1];
                const tasks = [];

                if (smartRetailReadOnlyGroupRole) {
                    if (smartSupermarketSharedGroup) {
                        let smartSupermarketSharedGroupPermissionForUsers = {
                            userGroupId: smartRetailUsersGroup.id,
                            roleId: smartRetailReadOnlyGroupRole.id,
                            entityGroupId: smartSupermarketSharedGroup.id,
                            entityGroupType: smartSupermarketSharedGroup.type
                        };
                        let smartSupermarketSharedGroupPermissionForAdmins = {
                            userGroupId: smartRetailAdministratorsGroup.id,
                            roleId: smartRetailReadOnlyGroupRole.id,
                            entityGroupId: smartSupermarketSharedGroup.id,
                            entityGroupType: smartSupermarketSharedGroup.type
                        };
                        tasks.push(roleService.saveGroupPermission(smartSupermarketSharedGroupPermissionForUsers));
                        tasks.push(roleService.saveGroupPermission(smartSupermarketSharedGroupPermissionForAdmins));
                    }
                    if (smartSupermarketGroup) {
                        let smartSupermarketGroupPermissionForAdmins = {
                            userGroupId: smartRetailAdministratorsGroup.id,
                            roleId: smartRetailReadOnlyGroupRole.id,
                            entityGroupId: smartSupermarketGroup.id,
                            entityGroupType: smartSupermarketGroup.type
                        };
                        tasks.push(roleService.saveGroupPermission(smartSupermarketGroupPermissionForAdmins));
                    }
                }

                if (smartRetailUserGenericRole) {
                    let smartRetailUserGenericPermission = {
                        userGroupId: smartRetailUsersGroup.id,
                        roleId: smartRetailUserGenericRole.id
                    };
                    tasks.push(roleService.saveGroupPermission(smartRetailUserGenericPermission));
                }

                if (smartRetailAdministratorGenericRole) {
                    let smartRetailAdminGenericPermission = {
                        userGroupId: smartRetailAdministratorsGroup.id,
                        roleId: smartRetailAdministratorGenericRole.id
                    };
                    tasks.push(roleService.saveGroupPermission(smartRetailAdminGenericPermission));
                }

                if (tasks.length) {
                    return widgetContext.rxjs.forkJoin(tasks);
                } else {
                    return widgetContext.rxjs.of(null);
                }
            }));

      }));

  }

  function getRoleByName(roleName, type) {
      var rolesPageLink = widgetContext.pageLink(10, 0, roleName);
      return roleService.getRoles(rolesPageLink, type, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((role) => role.name === roleName);
            } else {
                return null;
            }
          })
      );
  }

  function getEntityGroupByName(groupName, groupType) {
      var entityGroupsPageLink = widgetContext.pageLink(10, 0, groupName);
      return entityGroupService.getEntityGroupsByPageLink(entityGroupsPageLink, groupType, {ignoreLoading: true}).pipe(
          widgetContext.rxjs.map((data) => {
            if (data.data.length) {
                return data.data.find((group) => group.name === groupName);
            } else {
                return null;
            }
          })
      );
  }

  function saveAttributes(customer) {
    let attributesArray = [
        {
            key: 'address',
            value: getAddressValue(customer)
        }
    ];
    return attributeService.saveEntityAttributes(customer.id, "SERVER_SCOPE", attributesArray);
  }

  function getAddressValue(customer) {
      var address = '';
      if (customer.address) {
          address += customer.address;
      }
      if (customer.address2) {
          address += (address ? ', ' : '') + customer.address2;
      }
      if (customer.city) {
          address += (address ? ', ' : '') + customer.city;
      }
      if (customer.state) {
          address += (address ? ', ' : '') + customer.state;
      }
      if (customer.zip) {
          address += (customer.state ? ' ' : (address ? ', ' : '')) + customer.zip;
      }
      if (customer.country) {
          address += (address ? ', ' : '') + customer.country;
      }
      return address;
  }
}

