headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openAddDeviceDialog();

function openAddDeviceDialog() {
  customDialog.customDialog(htmlTemplate, AddDeviceDialogController).subscribe();
}

function AddDeviceDialogController(instance) {
  let vm = instance;

  vm.deviceTypes = [
      'Chiller',
      'Door Sensor',
      'Freezer',
      'Liquid Level Sensor',
      'Motion Sensor',
      'Occupancy Sensor',
      'Smart Bin',
      'Smart Shelf',
      'Smoke Sensor'
  ];

  vm.addDeviceFormGroup = vm.fb.group({
    name: ['', [vm.validators.required]],
    type: ['', [vm.validators.required]],
    label: ['', [vm.validators.required]]
  });

  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    var customerId = widgetContext.stateController.getStateParams().entityId;
    vm.addDeviceFormGroup.markAsPristine();
    getTargetDeviceGroups(customerId).pipe(
        widgetContext.rxjs.switchMap((targetDevicesGroup) => {
            var supermarketDevicesGroup = targetDevicesGroup[0];
            var unassignedDevicesGroup = targetDevicesGroup[1];
            return saveDeviceObservable(customerId, supermarketDevicesGroup, unassignedDevicesGroup);
        })
    ).subscribe(
      function (device) {
          saveAttributes(device.id).subscribe(
              function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
              }
        );
      }
    );
  };

  function saveDeviceObservable(customerId, supermarketDevicesGroup, unassignedDevicesGroup) {
        const formValues = vm.addDeviceFormGroup.value;
        let device = {
          name: formValues.name,
          type: formValues.type,
          label: formValues.label,
          customerId: customerId
        };
        return deviceService.saveDevice(device, unassignedDevicesGroup.id.id).pipe(
            widgetContext.rxjs.switchMap((savedDevice) => {
                return entityGroupService.addEntityToEntityGroup(supermarketDevicesGroup.id.id, savedDevice.id.id).pipe(
                    widgetContext.rxjs.map(() => {
                      return savedDevice;  
                    })
                );
            })
        );
  }

  function getTargetDeviceGroups(customerId) {
      return entityGroupService.getEntityGroupsByOwnerId(customerId.entityType, customerId.id, 'DEVICE').pipe(
          widgetContext.rxjs.switchMap((groups) => {
              return widgetContext.rxjs.forkJoin([
                  getOrCreateDevicesGroup(groups, 'Supermarket Devices', customerId),
                  getOrCreateDevicesGroup(groups, 'Unassigned Supermarket Devices', customerId)
              ]);
          })
      );
  }

  function getOrCreateDevicesGroup(groups, groupName, customerId) {
      var devicesGroup = groups.find(group => group.name === groupName);
      if (devicesGroup) {
          return widgetContext.rxjs.of(devicesGroup);
      } else {
          devicesGroup = {
              type: 'DEVICE',
              name: groupName,
              ownerId: customerId
          };
          return entityGroupService.saveEntityGroup(devicesGroup);
      }
  }

  function saveAttributes(entityId) {
    let attributesArray = [
        {
            key: 'state',
            value: 'normal'
        }
    ];
    return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
  }
}

