function deleteSupermarket() {
  var relatedDevicesQuery = {
      parameters: {
          rootId: entityId.id,
          rootType: entityId.entityType,
          direction: 'FROM'
      },
      filters: [{ relationType: 'Contains', entityTypes: ['DEVICE'] }]
  };
  entityRelationService.findByQuery(relatedDevicesQuery).subscribe((relatedDevicesRelations) => {
      var deleteDeviceObservables = [];
      relatedDevicesRelations.forEach((deviceRelation) => {
          deleteDeviceObservables.push(deviceService.deleteDevice(deviceRelation.to.id));
      });
      var deleteDevicesObservable = deleteDeviceObservables.length ? widgetContext.rxjs.forkJoin(deleteDeviceObservables) : widgetContext.rxjs.of(null);
      deleteDevicesObservable.subscribe(() => {
          assetService.deleteAsset(entityId.id).subscribe(
            function() {
                widgetContext.updateAliases();
            }
          );
      });
  });

}

