  customDialog.customDialog(htmlTemplate, EditUserDialogController).subscribe();
}

function EditUserDialogController(instance) {
  let vm = instance;

  vm.user = {};

  vm.editUserFormGroup = vm.fb.group({
    email: ['', [vm.validators.required, vm.validators.pattern(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\_\-0-9]+\.)+[a-zA-Z]{2,}))$/)]],
    firstName: [null],
    lastName: [null]
  });

  getUser();

  vm.cancel = function () {
    vm.dialogRef.close(null);
  };

  vm.save = function () {
    vm.editUserFormGroup.markAsPristine();
    saveUserObservable().subscribe(
      function () {
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
      }
    );
  };

  function getUser() {
      userService.getUser(entityId.id).subscribe(
          (user) => {
                vm.user = user;          
                vm.editUserFormGroup.patchValue({
                  email: vm.user.email,
                  firstName: vm.user.firstName,
                  lastName: vm.user.lastName
                }, {emitEvent: false});
          }
    );
  }

  function saveUserObservable() {
      const formValues = vm.editUserFormGroup.value;
      vm.user.email = formValues.email;
      vm.user.firstName = formValues.firstName;
      vm.user.lastName = formValues.lastName;
      return userService.saveUser(vm.user);
