configuration.tbelScript:
var filterSegmentFlowing = calculateFilterSegment();
var drainSegmentFlowing = filterSegmentFlowing && msg.?ss_sandFilterMode == 2;
var heatSegmentFlowing = filterSegmentFlowing && !drainSegmentFlowing && msg.?ss_heatPumpIntakeValveOpened == 1;
var phFilterSegmentFlowing = calculatePhFilterSegment();
var fillSegmentFlowing = phFilterSegmentFlowing && msg.?ss_poolIntakeValveOpened == 1;

var newMsg = {};

newMsg.put("filterSegmentFlowing", filterSegmentFlowing);
newMsg.put("drainSegmentFlowing", drainSegmentFlowing);
newMsg.put("heatSegmentFlowing", heatSegmentFlowing);
newMsg.put("phFilterSegmentFlowing", phFilterSegmentFlowing);
newMsg.put("fillSegmentFlowing", fillSegmentFlowing);

return {msg: newMsg, metadata: metadata, msgType: msgType};

function calculateFilterSegment() {
    if (!(msg.?ss_waterPumpOutgoingValveOpened == 1 && msg.?ss_waterPumpRunning == 1)) {
        return false;
    }
    if (msg.?ss_poolDrainValveOpened == 1 && msg.?ss_waterLevel > 0) {
        return true;
    } 
    if (msg.?ss_poolWeirValveOpened == 1 && msg.?ss_waterLevel > 88) {
        return true;
    }
    if (msg.?ss_mainIntakeValveOpened == 1) {
        return true;
    }
    return false;
}

function calculatePhFilterSegment() {
    if (filterSegmentFlowing && !drainSegmentFlowing && msg.?ss_throughpassValveOpened == 1) {
        return true;
    }
    if (heatSegmentFlowing && msg.?ss_heatPumpOutgoingValveOpened == 1) {
        return true;
    }
    return false;
}
